import React, { Component } from 'react';
import List from './List';
import Aux from './hoc/Aux';
import classes from './App.module.css'
import Layout from './layout/Layout';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      term: 'Please write your todo\' here',
      items: []
    };
  }

  OnChange = (event) => {
    this.setState({ term: event.target.value})
  }

  clear = (event) => {
    this.setState({term: ''})
  }

  onSubmit = (event) => {
    event.preventDefault();
    this.setState({
      term: '',
      items: [...this.state.items, this.state.term]

    })
  }

  render () {
    return (
      <Layout>
      <Aux>
      <div>
        <form className={classes.App} onSubmit={this.onSubmit }>
        <input value={this.state.term} onChange={this.OnChange} onClick={this.clear}/>
        <button>Submit</button>
        </form>
        <List items={this.state.items} />
      </div>
      </Aux>
      </Layout>
    );
  }
}




export default App;
