import Aux from './hoc/Aux';


const textInput = (props) => {
    return (
      <Aux>
          <div>
          <input type="text" ref="myInput" />
          <input
            type="button"
            value="Alert the text input"
            onClick={this.handleClick}
          />
        </div>
        </Aux>
    )
}


