import React, { Component } from 'react';
import List from './List';
import Aux from './hoc/Aux';
import classes from './Apps.module.css'

class Apps extends Component {

  constructor(props) {
    super(props);
    this.state = {
      term: '',
      items: []
    };

  }

  OnChange = (event) => {
    this.setState({ term: event.target.value})
  }

  clear = (event) => {
    this.setState({term: ''})
  }

  onSubmit = (event) => {
    event.preventDefault()
    if (this.state.term !=='') {
        this.setState({
            term: '',
            items: [...this.state.items, this.state.term]

          })
    }

  }

  removeItems = (nameofItem) => {
      const remainingItems = this.state.items.filter (li => {
          return li !== nameofItem;
      });
      this.setState({
          items: remainingItems
      });
  }
//   removeItems =(name) =>{
//     this.setState(prevState => ({
//         items: this.state.items.filter(el => el !== name)
//     }));
// }
  render () {
    return (
      <Aux>
      <div>
        <form className={classes.Apps} onSubmit={this.onSubmit}>
        <input value={this.state.term} onChange={this.OnChange} />
        <button><b>Submit</b></button>
        </form>
        <List items={this.state.items} removeItems={this.removeItems}/>
      </div>
      </Aux> 
    );
  }
}




export default Apps;
