import  React from 'react';
import classes from './List.module.css';
import Draggable from 'react-draggable';

class  List extends React.Component {
    delete = (e) => {
        this.props.removeItems(e);
    }
    render () {
        return (
            <div className={classes.block}>
            {/* {props.items.map((item, index) => <button key={index}>{item}</button>)} */}

            {/* {props.items.map((item, index) => <li key={index}>{item}</li>)} */}
            {/* onClick={this.delete.bind(this, index)} */}
        <ul className={classes.block}>
            {
                this.props.items.map((item, index) => 
                <Draggable axis="x"
                handle=".handle"
                defaultPosition={{x: 0, y: 0}}
                position={null}
                grid={[25, 25]}
                scale={1}
                onStart={this.handleStart}
                onDrag={this.handleDrag}
                onStop={this.handleStop}>>
                <p><button className={classes.button} onDoubleClick={()=> this.props.removeItem(index)}>{item} </button></p>
                </Draggable>
                )
            }
        </ul>
        </div>

        )
    }


    }

export default List;
