import  React from 'react';
import classes from './List.module.css';
import Draggable from 'react-draggable';
import ReactDragListView from 'react-drag-sortable';

class  List extends React.Component {
    constructor(props) {
        super(props);

        const data = [];
        for (let i = 1, len = 7; i < len; i++) {
          data.push({
            title: `rows${i}`
          });
        }

        this.state = {
          data
        };
      }
    delete = (e) => {
        this.props.removeItems(e);
    }
    render () {

        const that = this;
    const dragProps = {
      onDragEnd(fromIndex, toIndex) {
        const data = that.state.data;
        const item = data.splice(fromIndex, 1)[0];
        data.splice(toIndex, 0, item);
        that.setState({ data });
      },
      nodeSelector: 'li',
      handleSelector: 'a'
    };
        return (
            <div className={classes.block}>
            {/* {props.items.map((item, index) => <button key={index}>{item}</button>)} */}

            {/* {props.items.map((item, index) => <li key={index}>{item}</li>)} */}
            {/* onClick={this.delete.bind(this, index)} */}
        {/*<ul className={classes.block}>
            {
                this.props.items.map((item, index) => 
                <Draggable axis="y"

                defaultPosition={{x: 0, y: 0}}
                position={null}
                grid={[25, 25]}
                scale={1}
                onStart={this.handleStart}
                onDrag={this.handleDrag}
                onStop={this.handleStop}>
                <p><button className={classes.button} onDoubleClick={()=> this.props.removeItem(index)}>{item} </button></p>
                </Draggable>
                )
            }
        </ul>*/}
        <ReactDragListView {...dragProps}>
        <ol>
          {this.state.data.map((item, index) => (
            <li key={index}>
              {item.title}
              <a href="#">Drag</a>
            </li>
          ))}
        </ol>
      </ReactDragListView>

        </div>

        )
    }


    }

export default List;
